func GetUserByPageDto() ([]*entity.User, error) {
	userList := make([]*entity.User, 0)
	conn := common.GoManagerConfig.MySQLConn
	query := conn.Find(&userList).Limit(0).Limit(1)
	if query.Error != nil {
		log.Printf("查询发生异常，error=%s\n", query.Error)
		return nil, query.Error
	}
	return userList, nil
}

func DeleteUserById(id int32) bool {
	conn := common.GoManagerConfig.MySQLConn
	user := entity.User{
		Id: id,
	}
	delRes := conn.Delete(&user)
	if delRes.Error != nil {
		log.Printf("查询发生异常，error=%s\n", delRes.Error)
		return false
	}
	return delRes.RowsAffected == 1
}

